stack = []def push(stack):    number = int(input('какое число добавить?:'))    if len(stack) < 100:        print('ok')        stack += [number]        return stack    else:        print('Стэк переполнен.')        return stackdef pop(stack):    print(stack.pop())    return stackdef test_pop():    if pop([1, 2, 3]) == [1, 2]:        print('test pop ok')def back(stack):    print (stack[-1])def test_back():    if back([1, 2, 5]) == [5]:        print('test back ok')def clear(stack):    stack[:] = []    print('ok')    return stackdef test_clear():    if clear(stack) == []:        print('test clear ok')def size(stack):    size=len(stack)    print(size)    return stackdef test_size():    if size([1, 2, 5]) == [3]:        print('test size ok')def exit(stack):    print('bye')    quit()while True:    choice = input('что сделать со стеком?(push,pop,back,size,clear,exit):')    if choice == 'push':        stack = push(stack)    elif choice == 'pop':        stack = pop(stack)    elif choice == 'back':        back(stack)    elif choice == 'size':        size(stack)    elif choice == 'clear':        stack = clear(stack)    elif choice == 'exit':        exit(stack)